import "bootstrap/dist/css/bootstrap.min.css";
import API from "../../Utils/API/API";
import Container from "react-bootstrap/Container";
import { useState, useEffect } from "react";
import { Card, CardColumns, CardGroup } from "react-bootstrap";
import RepoCard from "../../components/Card/RepoCard.js";
import TopCard from "../../components/Card/TopCard";

function Projects() {
  const [state, setState] = useState({ repos: [] });
  useEffect(() => {
    const remoteApiPromise = API.getRepos();
    remoteApiPromise.then((items) => {
      console.log(items);
      const transfromedData = items.data;
      console.log(transfromedData);
      setState((prevState) => ({
        ...prevState,
        repos: transfromedData,
      }));
    });
  }, []);

  return (
    <Container fluid='true'>
        <TopCard/>
      <CardColumns>
        {state.repos.map((item) => (
          <RepoCard
            Name={item.name}
            Description={item.description}
            url={item.url}
          ></RepoCard>
        ))}
      </CardColumns>
    </Container>
  );
}
export default Projects;
